        time: '4',
        comments: '40'
    },
    {
        id: 3,
        title: `Abc`,
        url: 'facebook.com',
        points: '140',
        author: 'chu',
        time: '2',
        comments: '21'
    },
    {
        id: 4,
        title: `Abc`,
        url: 'facebook.com',
        points: '140',
        author: 'chu',
        time: '2',
        comments: '21'
    }
];



ReactDOM.render( 
    <ThreadList files={testCards}/>, 
    document.querySelector('#root')
)
